import { useState } from 'react';
import './App.css';

function Square({ }) {

  const[value,setValue]=useState();
  const[count,setCount]=useState(1);

  function handleClick() {
    if(count%2==0){
      setValue("X");
    }else{
      setValue("O");
    }
    setCount(count+1);
    console.log(count+'clicked!');
  }

  return (
    <button className="square" onClick={handleClick}>
      {value}
    </button>
  );
}

export default function App(){
    return(
      <div>
        <div className='row'>
          <Square/>
          <Square/>
          <Square/>
        </div>

        <div className='row'>
          <Square/>
          <Square/>
          <Square/>
        </div>

        <div className='row'>
          <Square/>
          <Square/>
          <Square/>
        </div>
      </div>
    );
};
