import { useState } from 'react';
import './App.css';

let count=1;

function Square() {
  const[value,setValue]=useState();

  function handle(){
    if(count%2===0){
      setValue("X");
    }else{
      setValue("O");
    }
    count++;
  }
  return(
    <div>
      <button className='square' onClick={handle}>
        {value}
      </button>
    </div>
  );
}

export default function App(){
    return(
      <div>
        <div></div>

        <div></div>

        <div></div>
        <Square/>
      </div>
    );
};
